#if defined(_WIN32)
int daylight = 0;
long timezone = 0;
char* tzname[2] = {(char*)"Local", (char*)"Local"};
#define tzset _tzset
#endif

const TimeZoneInfo& TimeZoneInfo::Local() {
  if (local.id != (const char*)tzname[0]) {
    try {
      tzset();
      FillSystemTimeZones(systemTimeZones);
      local = TimeFindSystemTimeZoneById((const char*)tzname[0]);
    } catch (...) {
      local.id = (const char*)tzname[0];
      local.baseUtcOffset = (int64) - timezone * 10000000;
      local.daylightName = (const char*)tzname[1];
      local.displayName = string::Format("(GMT{0}{1}) {2}", -timezone ? "+" : "", TimeSpan::FromTicks((int64) - timezone * 10000000), (const char*)tzname[0]);
      local.standardName = (const char*)tzname[0];
      local.supportsDaylightSavingTime = daylight != 0;
    }
  }
  return local;
}

const TimeZoneInfo& TimeZoneInfo::Utc() {
  return utc;
}

const Collections::Generic::List<TimeZoneInfo>& TimeZoneInfo::GetSystemTimeZones() {
  FillSystemTimeZones(systemTimeZones);
  return systemTimeZones;
}

const TimeZoneInfo& TimeZoneInfo::TimeFindSystemTimeZoneById(const String& id) {
  if (local.id == id)
    return local;

  if (utc.id == id)
    return utc;

  for (const TimeZoneInfo& item : systemTimeZones)
    if (item.id == id)
      return item;

  throw TimeZoneNotFoundException(caller_);
}


